import axios from 'axios';
import { isDateWithinInterval, compareDates } from '../../functions/datetime';
import { serializeEvent } from '../../functions/serializers';

const apiUrl = 'https://localhost:9292/';

const state = {
  events: [],
  event: null,
  isEditMode: false,
  clickedDate: null,
  participants: [],
};

const getters = {
  events: state =>
  state.events.filter(event => event).map(event => serializeEvent(event)),
  event: state => serializeEvent(state.event),
  dayEvents: state =>
  state.events
  .map(event => serializeEvent(event))
  .filter(event =>
  isDateWithinInterval(state.clickedDate, event.startDate, event.endDate)
  )
  .sort(compareDates),
  isEditMode: state => state.isEditMode,
  clickedDate: state => state.clickedDate,
  participants: state => state.participants.map(participant => {
    return {
      ...participant,
      start: new Date(participant.event.start),
      end: new Date(participant.event.end)
    };
  }),
};

const mutations = {
  setEvents: (state, events) => (state.events = events),
  appendEvent: (state, event) => (state.events = [...state.events, event]),
  setEvent: (state, event) => (state.event = event),
  removeEvent: (state, event) =>
    (state.events = state.events.filter(e => e.id !== event.id)),
  resetEvent: state => (state.event = null),
  updateEvent: (state, event) =>
    (state.events = state.events.map(e => (e.id === event.id ? event : e))),
  setEditMode: (state, bool) => (state.isEditMode = bool),
  setClickedDate: (state, date) => (state.clickedDate = date),
  setParticipants: (state, participants) => (state.participants = participants),
  appendParticipant: (state, participant) =>
    (state.participants = [...state.participants, participant]),
  removeParticipant: (state, participant) =>
    (state.participants = state.participants.filter(
      p => p.id !== participant.id
    )),
  updateParticipant: (state, participant) =>
    (state.participants = state.participants.map(p =>
      p.id === participant.id ? participant : p
    ))
};

const actions = {
  async fetchEvents({ commit }) {
    const response = await axios.get(`${apiUrl}/events`);
    commit('setEvents', response.data);
  },
  async createEvent({ commit }, event) {
    console.log('createEvent', event);
    const response = await axios.post(`${apiUrl}/events`, event);
    commit('appendEvent', response.data);
  },
  async deleteEvent({ commit }, id) {
    const response = await axios.delete(`${apiUrl}/events/${id}`);
    commit('removeEvent', response.data);
    commit('resetEvent');
  },
  async updateEvent({ commit }, event) {
    const response = await axios.put(`${apiUrl}/events/${event.id}`, event);
    commit('updateEvent', response.data);
  },
  setEvent({ commit }, event) {
    commit('setEvent', event);
  },
  setEditMode({ commit }, bool) {
    commit('setEditMode', bool);
  },
  setClickedDate({ commit }, date) {
    commit('setClickedDate', date);
  },
  async fetchParticipants({ commit }) {
    const response = await axios.get(`${apiUrl}/participants`);
    commit('setParticipants', response.data);
  },
  async createParticipant({ commit }, participant) {
    console.log('createParticipant', participant);
    const response = await axios.post(`${apiUrl}/participants`, participant);
    commit('appendParticipant', response.data);
  },
  async deleteParticipant({ commit }, participantId) {
    const response = await axios.delete(
      `${apiUrl}/participants/${participantId}`
    );
    commit('removeParticipant', response.data);
  },
  async updateParticipant({ commit }, participant) {
    const response = await axios.put(
      `${apiUrl}/participants/${participant.id}`,
      participant
    );
    commit('updateParticipant', response.data);
  }
};

export default {
  namespaced: true,
  state,
  getters,
  mutations,
  actions
};
