import axios from 'axios';
// import { serializeParticipant } from '../../functions/serializers';
const apiUrl = 'https://localhost:9292/';

const state = {
  participantsEvent: [],
  participantsUser: []
};

const getters = {
  participantsEvent: state => state.participantsEvent.map(participantsEvent => {
    return {
      ...participantsEvent,
      start: new Date(participantsEvent.start),
      end: new Date(participantsEvent.end)
    };
  }),
  participantsUser: state => state.participantsUser
};

const mutations = {
  setParticipantsEvent: (state, participants) => (state.participantsEvent = participants),
  appendParticipantEvent: (state, participant) =>
    (state.participantsEvent = [...state.participantsEvent, participant]),
  updateParticipantEvent: (state, participant) =>
    (state.participantsEvent = state.participantsEvent.map(p =>
      p.id === participant.id ? participant : p
    )),
  removeParticipantEvent: (state, participantId) =>
    (state.participantsEvent = state.participantsEvent.filter(
      p => p.id !== participantId
    )),
  setParticipantsUser: (state, participants) => (state.participantsUser = participants),
  appendParticipantUser: (state, participant) =>
    (state.participantsUser = [...state.participantsUser, participant]),
  updateParticipantUser: (state, participant) =>
    (state.participantsUser = state.participantsUser.map(p =>
      p.id === participant.id ? participant : p
    )),
  removeParticipantUser: (state, participantId) =>
    (state.participantsUser = state.participantsUser.filter(
      p => p.id !== participantId
    ))
};

const actions = {
  async fetchParticipantEvent({ commit }) {
    const response = await axios.get(`${apiUrl}/participants/event_excerpt/`);
    commit('setParticipantsEvent', response.data);
  },
  async createParticipantEvent({ commit }, participant) {
    console.log('createParticipantEvent', participant);
    const response = await axios.post(`${apiUrl}/participants/event_excerpt/`, participant);
    commit('appendParticipantEvent', response.data);
  },
  async updateParticipantEvent({ commit }, participant) {
    const response = await axios.put(
      `${apiUrl}/participants/event_excerpt/${participant.id}`,
      participant
    );
    commit('updateParticipantEvent', response.data);
  },
  async deleteParticipantEvent({ commit }, participantId) {
    const response = await axios.delete(
      `${apiUrl}/participants/event_excerpt/${participantId}`
    );
    commit('removeParticipantEvent', response.data);
  },
  async fetchParticipantUser({ commit }) {
    const response = await axios.get(`${apiUrl}/participants/user_excerpt/`);
    commit('setParticipantsUser', response.data);
  },
  async createParticipantUser({ commit }, participant) {
    console.log('createParticipantUser', participant);
    const response = await axios.post(`${apiUrl}/participants/user_excerpt/`, participant);
    commit('appendParticipantUser', response.data);
  },
  async updateParticipantUser({ commit }, participant) {
    const response = await axios.put(
      `${apiUrl}/participants/user_excerpt/${participant.id}`,
      participant
    );
    commit('updateParticipantUser', response.data);
  },
  async deleteParticipantUser({ commit }, participantId) {
    const response = await axios.delete(
      `${apiUrl}/participants/user_excerpt/${participantId}`
    );
    commit('removeParticipantUser', response.data);
  },
};

export default {
  namespaced: true,
  state,
  getters,
  mutations,
  actions
};
