import axios from 'axios'

const apiUrl = 'https://localhost:9292/';

const state = {
  groups: []
};

const getters = {
  usersByCalendarId: (state) => (calendarId) => {
    const group = state.groups.find(group => group.calendar_id === calendarId)
    if (group) {
      return group.users
    } else {
      return []
    }
  }
};

const mutations = {
  setGroups(state, groups) {
    state.groups = groups
  }
};

const actions = {
  async fetchGroups({ commit }) {
    try {
      const response = await axios.get(`${apiUrl}/groups`)
      commit('setGroups', response.data)
    } catch (error) {
      console.error(error)
    }
  }
};


export default {
  namespaced: true,
  getters,
  state,
  mutations,
  actions,
};
