  data: function data() {
    return {
      selectedColor: null
    };
  },
  computed: {
    label: function label() {
      switch (this.selectedColor) {
        case "red":
          return "赤色の会議";

        case "blue":
          return "青色の会議";

        case "green":
          return "緑色の会議";

        default:
          return "";
      }
    }
  }
};
