export default {
  name: 'GroupCheckBox',
  components: {
    GroupCheckBox: GroupCheckBox
  },
  data: function data() {
    return {
      users: []
    };
  },
  methods: {
    userListLoad: function userListLoad() {
      var _this = this;

      axios.get('https://example.com/users').then(function (result) {
        Object.entries(result.users).forEach(function (user) {
          return _this.users.push({
            name: user["name"],
            id: user["id"]
          });
        });
      }, function (err) {
        console.log(err);
      });
    }
  }
};
