import moment from 'moment';
import Datetime from 'vue-ctk-date-time-picker';
import '@/node_modules/vue-ctk-date-time-picker/dist/vue-ctk-date-time-picker.css';
export default {
  components: {
    Datetime: Datetime
  },
  data: function data() {
    return {
      requestDate: ''
    };
  },
  computed: {
    start: function start() {
      // min-date に明日の9時を指定
      var start = moment().add(1, 'days').hour(8);
      return start.format('YYYY-MM-DDTHH:mm:ss');
    },
    end: function end() {
      // max-date に min-date から3ヶ月後を指定
      var start = moment(this.start);
      var end = start.add(3, 'months').endOf('day');
      return end.format('YYYY-MM-DDTHH:mm:ss');
    }
  }
};
